custom_css:
/* Example CSS for your font: jetbrainsmono-mediumitalic */

/* Apply to all text */
:root * {
	--font-default: 'jetbrainsmono-mediumitalic';
	--default-font: 'jetbrainsmono-mediumitalic';
	--font-family-editor: 'jetbrainsmono-mediumitalic';
	--font-interface-override: 'jetbrainsmono-mediumitalic';
	--font-text-override: 'jetbrainsmono-mediumitalic';
}

/* Example: Apply to custom CSS class */
.custom-font * {
	font-family: 'jetbrainsmono-mediumitalic' !important;
}

/* Example: Apply to specific elements only */
.custom-font h1, .custom-font h2, .custom-font h3 {
	font-family: 'jetbrainsmono-mediumitalic' !important;
}
